SELECT eq.id id, eq.equipment_sn equipmentSn, eq.equipment_name equipmentName, eq.equipment_type equipmentTypeCode, cat1.name equipmentType, cat2.name equipmentCategory, eq.equipment_mode equipmentMode, (SELECT name from sys_category where code=eq.equipment_mode) equipmentModeName, eq.equipment_specs equipmentSpecs, (SELECT name from sys_category where code=eq.equipment_specs) equipmentSpecsName, cat3.name equipmentSection, eq.equipment_location equipmentLocation, sup.supplier_name equipmentSupplier, eq.equipment_supplier equipmentSupplierId, cat4.name equipmentStatus, eq.equipment_img equipmentImg, eq.equipment_operating equipmentOperating, eq.equipment_purchase equipmentPurchase, eq.equipment_revstop equipmentRevstop, (SELECT item_text FROM sys_dict_item WHERE dict_id = ( SELECT id FROM sys_dict WHERE dict_code = 'working_status' )AND item_value = eq.equipment_revstop) as equipmentRevstopText, ol.id as labelId, ISNULL(ot.id) as bindStatus, ot.id otId, (select config_value from system_config where config_key='upkeeper.remind.days') countDays, (SELECT GROUP_CONCAT(user_id) FROM opt_equipment_person_responsible where equipment_id=eq.id) as personResponsible, (select GROUP_CONCAT(realname) FROM sys_user WHERE id in (SELECT user_id FROM opt_equipment_person_responsible where equipment_id=eq.id)) as personResponsibleName, ol.label_name as labelName, olt.id oltId, olt.name, olt.parent_id, olt.parent_ids FROM opt_equipment eq LEFT JOIN sys_category cat1 on cat1.code=eq.equipment_type LEFT JOIN sys_category cat2 on cat2.code=eq.equipment_category LEFT JOIN sys_category cat3 on cat3.code=eq.equipment_section LEFT JOIN sys_category cat4 on cat4.code=eq.equipment_status LEFT JOIN opt_supplier sup on sup.id=eq.equipment_supplier left join opt_equipment_label oel on oel.equipment_id = eq.id and valid_status = 1 left join opt_label ol on ol.id = oel.label_id left join opt_obj_tree ot on ot.data_id = eq.id and ot.del_flag = '0' and ot.centre = #{param.centre} and ot.obj_id = (select id from sys_object where obj_type = #{param.objType} and del_flag = 0) left join opt_label_tree olt on olt.id = ot.tree_id and olt.del_flag = '0' where eq.del_flag='0'
and eq.equipment_sn like concat('%',#{param.equipmentSn},'%')
and eq.equipment_supplier=#{param.equipmentSupplier}
and eq.equipment_type=#{param.equipmentType}
and eq.equipment_revstop=#{param.equipmentRevstop}
and eq.equipment_section=#{param.optSection}
and eq.equipment_location like concat('%',#{param.optLocation},'%')
and eq.equipment_name like concat('%',#{param.optName},'%')
and eq.equipment_category = #{param.equipmentCategory}
and (olt.id in
#{item}
or olt.id in (select id from opt_label_tree where parent_ids REGEXP #{param.parentIdsQueryStr}))
and (eq.equipment_sn is null or eq.equipment_sn = '')
and eq.equipment_sn=#{attr.equipmentSn}
and (eq.equipment_name is null or eq.equipment_name = '')
and eq.equipment_name=#{attr.equipmentName}
and (cat2.name is null or cat2.name = '')
and cat2.name=#{attr.equipmentCategory}
and (cat1.name is null or cat1.name = '')
and cat1.name=#{attr.equipmentType}
and ((SELECT name from sys_category where code=eq.equipment_mode) is null or (SELECT name from sys_category where code=eq.equipment_mode) = '')
and (SELECT name from sys_category where code=eq.equipment_mode)=#{attr.equipmentModeName}
and ((SELECT name from sys_category where code=eq.equipment_specs) is null or (SELECT name from sys_category where code=eq.equipment_specs) = '')
and (SELECT name from sys_category where code=eq.equipment_specs)=#{attr.equipmentSpecsName}
and (cat3.name is null or cat3.name = '')
and cat3.name=#{attr.equipmentSection}
and (eq.equipment_location is null or eq.equipment_location = '')
and eq.equipment_location=#{attr.equipmentLocation}
and (sup.supplier_name is null or sup.supplier_name = '')
and sup.supplier_name=#{attr.equipmentSupplier}
and (eq.equipment_supplier is null or eq.equipment_supplier = '')
and eq.equipment_supplier=#{attr.equipmentSupplierId}
and (cat4.name is null or cat4.name = '')
and cat4.name=#{attr.equipmentStatus}
and ((SELECT item_text FROM sys_dict_item WHERE dict_id = ( SELECT id FROM sys_dict WHERE dict_code = 'working_status' ) and (item_value = eq.equipment_revstop)) is null or (SELECT item_text FROM sys_dict_item WHERE dict_id = ( SELECT id FROM sys_dict WHERE dict_code = 'working_status' ) and (item_value = eq.equipment_revstop)) = '')
and (SELECT item_text FROM sys_dict_item WHERE dict_id = ( SELECT id FROM sys_dict WHERE dict_code = 'working_status' ) and (item_value = eq.equipment_revstop))=#{attr.equipmentRevstopText}
and (eq.equipment_revstop is null or eq.equipment_revstop = '')
and eq.equipment_revstop=#{attr.equipmentRevstop}
and (eq.equipment_img is null or eq.equipment_img = '')
and eq.equipment_img=#{attr.equipmentImg}
and (eq.equipment_specs is null or eq.equipment_specs = '')
and eq.equipment_specs=#{attr.equipmentSpecs}
and (eq.equipment_mode is null or eq.equipment_mode = '')
and eq.equipment_mode=#{attr.equipmentMode}
and (eq.equipment_type is null or eq.equipment_type = '')
and eq.equipment_type=#{attr.equipmentTypeCode}
and (ISNULL(ot.id) is null or ISNULL(ot.id) = '')
and ISNULL(ot.id)=#{attr.bindStatus}
and (ol.id is null or ol.id = '')
and ol.id=#{attr.labelId}
and (ol.label_name is null or ol.label_name = '')
and ol.label_name=#{attr.labelName}
and ((select config_value from system_config where config_key='upkeeper.remind.days') is null or (select config_value from system_config where config_key='upkeeper.remind.days') = '')
and (select config_value from system_config where config_key='upkeeper.remind.days')=#{attr.countDays}
and (ol.label_name is null or ol.label_name = '')
and ol.label_name=#{attr.labelName}
and ((SELECT GROUP_CONCAT(user_id) FROM opt_equipment_person_responsible where equipment_id=eq.id) is null or (SELECT GROUP_CONCAT(user_id) FROM opt_equipment_person_responsible where equipment_id=eq.id) = '')
and (SELECT GROUP_CONCAT(user_id) FROM opt_equipment_person_responsible where equipment_id=eq.id)=#{attr.personResponsible}
and ((select GROUP_CONCAT(realname) FROM sys_user WHERE id in (SELECT user_id FROM opt_equipment_person_responsible where equipment_id=eq.id)) is null or (select GROUP_CONCAT(realname) FROM sys_user WHERE id in (SELECT user_id FROM opt_equipment_person_responsible where equipment_id=eq.id)) = '')
and (select GROUP_CONCAT(realname) FROM sys_user WHERE id in (SELECT user_id FROM opt_equipment_person_responsible where equipment_id=eq.id))=#{attr.personResponsibleName}
and eq.equipment_operating=#{attr.equipmentOperating}
and eq.equipment_purchase=#{attr.equipmentPurchase}
and (eq.equipment_operating is null or eq.equipment_operating = '')
and (eq.equipment_purchase is null or eq.equipment_purchase = '')



    and t.customer_name like concat('%',#{param.customerName},'%')


    and t.customer_sn like concat('%',#{param.customerSn},'%')


    and  date_format(t.create_time,'%Y-%m-%d')>=#{param.startTime}


    and  date_format(t.create_time,'%Y-%m-%d')<=#{param.endTime}

ORDER BY t.create_time desc

SELECT t.id id, t.customer_name customerName, t.customer_sn customerSn, t.customer_address customerAddress, t.customer_type customerType, t1.item_text customerTypeName, t.customer_status customerStatus, t2.item_text customerStatusName, cat1.name priceMode, cat2.name payMode, ISNULL(ot.id) as bindStatus, t.price price, t.create_time createTime, t.custome_duty customeDuty, t.custome_bank_accout customeBankAccout FROM settle_customer t LEFT JOIN sys_category cat1 on t.price_mode=cat1.code LEFT JOIN sys_category cat2 on t.pay_mode=cat2.code LEFT JOIN v_sys_dict_item t1 on t.customer_type=t1.item_value and t1.dict_code='customer_type' LEFT JOIN v_sys_dict_item t2 on t.customer_status=t2.item_value and t2.dict_code='customer_status' left join opt_obj_tree ot on ot.data_id = t.id and ot.del_flag = '0' and ot.centre = #{param.centre} and ot.obj_id = (select id from sys_object where obj_type = #{param.objType} and del_flag = 0) left join opt_label_tree olt on olt.id = ot.tree_id and olt.del_flag = '0' where t.del_flag='0'
and t.customer_name like concat('%',#{param.customerName},'%')
and t.customer_sn like concat('%',#{param.customerSn},'%')
and date_format(t.create_time,'%Y-%m-%d')>=#{param.startTime}
and date_format(t.create_time,'%Y-%m-%d')<=#{param.endTime}
and (olt.id in
#{item}
or olt.id in (select id from opt_label_tree where parent_ids REGEXP #{param.parentIdsQueryStr}))
and (t.customer_sn is null or t.customer_sn = '')
and t.customer_sn=#{attr.customerSn}
and (t.customer_name is null or t.customer_name = '')
and t.customer_name=#{attr.customerName}
and (t.customer_address is null or t.customer_address = '')
and t.customer_address=#{attr.customerAddress}
and (t2.item_text is null or t2.item_text = '')
and t2.item_text=#{attr.customerStatusName}
and (t1.item_text is null or t1.item_text = '')
and t1.item_text=#{attr.customerTypeName}
and (cat2.name is null or cat2.name = '')
and cat2.name=#{attr.payMode}
and (t.custome_duty is null or t.custome_duty = '')
and t.custome_duty=#{attr.customeDuty}
and (t.custome_bank_accout is null or t.custome_bank_accout = '')
and t.custome_bank_accout=#{attr.customeBankAccout}
and (t.customer_type is null or t.customer_type = '')
and t.customer_type=#{attr.customerType}
and (cat1.name is null or cat1.name = '')
and cat1.name=#{attr.priceMode}
and (t.price is null or t.price = '')
and t.price=#{attr.price}
and (t.customer_status is null or t.customer_status = '')
and t.customer_status=#{attr.customerStatus}
